import { NavigationContainer, StackNavigationState, useNavigation } from '@react-navigation/native';
import { NativeStackNavigationProp } from '@react-navigation/native-stack';
import { StatusBar } from 'expo-status-bar';
import { useState} from 'react';
import { Pressable, StyleSheet, Text, View } from 'react-native';
import { Icon } from 'react-native-elements';
import { RootStackParamList } from '../stackParams';
import {useEmenta} from '../contexts/Ementa';




type RequestScreenProp = NativeStackNavigationProp<RootStackParamList, 'RequestScreen'>;


export default function RequestScreen() {
  const navigation = useNavigation<RequestScreenProp>()
  const {ementa, setEmenta} = useEmenta();

  const [count, setCount] = useState(1)

  function subtractcount() {
    if (count > 1) {
      setCount(count - 1)
    }
  }
  function addcount() {
    setCount(count + 1)
  }

  return (

    <View style={styles.container} >
      <Text style={styles.title}>App Name</Text>
      <View style={styles.countContainer}>
        <Pressable onPress={subtractcount}><Icon name="minuscircleo" size={80} color="black" type="antdesign" /></Pressable>
        <Text style={styles.text}>{count}</Text>
        <Pressable onPress={addcount}><Icon name="pluscircleo" size={80} color="black" type="antdesign" /></Pressable>
      </View>
      <Pressable onPress={() => {
        navigation.navigate('Ingredients',{number: count})}} style={styles.calculateContainer}>Calculate</Pressable>
      <Pressable style={styles.allContainer}>All</Pressable>

      <StatusBar style="auto" />

    </View>

  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative',
  },
  text: {
    fontSize: 50,
    paddingVertical: 12,
    paddingHorizontal: 4,
    borderRadius: 10,
    borderWidth: 3,
    borderColor: 'black',
    elevation: 3,
    textAlign: 'center',
    width: 70,
    marginLeft: 50,
    marginRight: 50


  },
  countContainer: {
    flex: 1,
    flexDirection: 'row',
    flexWrap: 'wrap',
    position: 'absolute',
    top: 250,


  },
  calculateContainer: {
    position: 'absolute',
    top: 400,
    fontSize: 30,
    paddingVertical: 12,
    paddingHorizontal: 4,
    borderRadius: 10,
    borderWidth: 3,
    borderColor: 'black',
    elevation: 3,
    justifyContent: 'space-between'
  },
  allContainer: {
    position: 'absolute',
    top: 600,
    fontSize: 30,
    paddingVertical: 12,
    paddingHorizontal: 50,
    borderRadius: 10,
    borderWidth: 3,
    borderColor: 'black',
    elevation: 3,
    justifyContent: 'space-between'
